import React, { useState, useContext } from 'react';
import ThemeContext from '../context/ThemeContext'


const Automatic = () => {
    const context = useContext(ThemeContext);

    clock = () => { let i = 0;
        setInterval(() => {
          if (context[0] === 'light') {
            context[0] = "dark"
            i++;
          }
          else {
            context[0] = "light";
          }

        }, 5000);
        }
    return (
        null
    )
}

export default Automatic;
