import React, { useState, useContext } from "react"
import "./TodoItem.scss";
import Priority from "../Priority/Priority"
import { Link } from "react-router-dom"
import ThemeContext from "../../context/ThemeContext";

const lowStyle = {
    opacity: "0.7"
}
const highStyle = {
    fontWeight: "500"
}

const darkbutton = {

}

const TodoItem = (props) => {
    const context = useContext(ThemeContext);
    const [priority, setPriority] = useState("")
    // const isDark = this.context === "dark" ? true : false;
    const onChangeHandler = (e) => {
        setPriority(e.target.value)
    }
    return (
        <li className="todo-item">
            <input
                type="checkbox"
                checked={props.completed}
                onChange={() => props.handleChange(props._id)}>
            </input>
            {priority === "High" ? <div style={{
                width:"12px", 
                height:"12px", 
                borderRadius: "12px", 
                background:"red",
                display: "inline-block",
                marginRight: "4px"
                }}></div> : null}
                {priority === "Medium" ? <div style={{
                width:"12px", 
                height:"12px", 
                borderRadius: "12px", 
                background:"yellow",
                display: "inline-block",
                marginRight: "4px"
                }}></div> : null}
                {priority === "Low" ? <div style={{
                width:"12px", 
                height:"12px", 
                borderRadius: "12px", 
                background:"purple",
                display: "inline-block",
                marginRight: "4px"
                }}></div> : null}
            <span 
                className={props.completed ? "completedStyle" : null}
                style= { priority === "Low"  ? lowStyle 
                : priority === "High"  ? highStyle : null}
            >
                <Link className="styleLink" to={`/${props._id}`}>{props.title}</Link>

            </span>

            <button onClick={() => props.deleteTodoHandler(props._id)}>Delete</button>
            <span>{this.context}</span>
            <Priority onChangeHandler={onChangeHandler}/>

        </li>
    )
}

export default TodoItem
